INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514968, 'Halothane', 'Fospropofol', 'Moderate', 'Additive central nervous system and cardiorespiratory depressant effects may occur when fospropofol or propofol is administered with other depressants such as sedative-hypnotic agents and narcotic analgesics.', 'DDInter', 1767317477, 'Patients should be monitored closely for excessive sedation and cardiorespiratory depression, and the medication dosage(s) adjusted accordingly. Supportive therapy should be provided if needed.', 'Synergism', 'Patients should be monitored closely for excessive sedation and cardiorespiratory depression, and the medication dosage(s) adjusted accordingly.', NULL, NULL, 'Moderate', 'pharmacodynamic');
