INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514978, 'Halothane', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Lonafarnib is a potent CYP450 3A time-dependent and mechanism-based inhibitor in vitro.', 'DDInter', 1767317477, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. The prescribing information recommends avoiding concomitant use of lonafarnib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges when possible. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514979, 'Halothane', 'Mesoridazine', 'Major', 'Mesoridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Mesoridazine overdosage has been associated with ventricular arrhythmias and death. The extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with mesoridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 1767317477, 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514980, 'Halothane', 'Metocurine', 'Moderate', 'Inhalation anesthetics may potentiate the effects of nondepolarizing muscle relaxants. Long-acting muscle relaxants such as pancuronium and d-tubocurarine are more affected than other agents.', 'DDInter', 1767317477, 'The muscle relaxant dosage may need to be reduced when inhalation anesthetics are used, especially during longer procedures. In general, inhalation anesthetics should be administered only by health care providers specially trained in the use of these agents and in the care of anesthetized patients.', 'Synergism', 'The muscle relaxant dosage may need to be reduced when inhalation anesthetics are used, especially during longer procedures.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514981, 'Halothane', 'Nitrous oxide', 'Minor', 'Coadministration of 70% nitrous oxide reduces the dosage requirements for halogenated inhalation anesthetics. Minimum alveolar concentration (MAC) values can be reduced from 0.75% to 0.29% for halothane, from 1.68% to 0.6% for enflurane, and from 1.15% to 0.5% for isoflurane. The MAC equivalent dose for sevoflurane is reduced by approximately 50% in the presence of 50% nitrous oxide.', 'DDInter', 1767317477, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1514982, 'Halothane', 'Oxazepam', 'Minor', 'Benzodiazepines may be expected to decrease the minimum alveolar concentration required for inhalation anesthetics. When these agents are used concomitantly, less inhalation anesthetic may be required. In general, anesthesia should be titrated to clinical effect by those experienced in anesthesia technique.', 'DDInter', 1767317477, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
