INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1515729, 'Norgestrel', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Lonafarnib is a potent CYP450 3A time-dependent and mechanism-based inhibitor in vitro.', 'DDInter', 1767317477, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. The prescribing information recommends avoiding concomitant use of lonafarnib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges when possible. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1515730, 'Ospemifene', 'Lonafarnib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of ospemifene, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised when ospemifene is used with potent CYP450 3A4 inhibitors. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain, shortness of breath, and pain or swelling in the arms or legs.', 'Metabolism', 'Caution is advised when ospemifene is used with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1515731, 'Paclitaxel (protein-bound)', 'Lonafarnib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of paclitaxel, which has been found in vitro to be a substrate of these isoenzymes.', 'DDInter', 1767317477, 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', 'Metabolism', 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
