INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1515857, 'Oxytetracycline', 'Magnesium hydroxide', 'Moderate', 'INTERVAL: Administration of a tetracycline with aluminum, calcium, or magnesium salts significantly decreases tetracycline serum concentrations. The proposed mechanism is chelation of tetracycline by the cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract. The interaction has also been reported with parenteral doxycycline and oral antacids.', 'DDInter', 1767317477, 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', 'Absorption', 'The administration of tetracyclines and preparations containing aluminum, magnesium, or calcium should be separated by two to four hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1515858, 'Penbutolol', 'Magnesium hydroxide', 'Minor', 'Concurrent administration with aluminum and magnesium antacids has been shown to decrease the oral bioavailability of certain beta-blockers, although data are conflicting. The exact mechanism of interaction is unknown but may involve cation binding of beta-blockers or a reduction in the dissolution rate due to increased gastric pH. Based on available data, the clinical significance of this potential interaction is difficult to determine. As a precaution, patients may want to consider separating the administration times of beta-blockers and antacids or other aluminum- or magnesium-containing products by at least 2 hours.', 'DDInter', 1767317477, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1515859, 'Perphenazine', 'Magnesium hydroxide', 'Moderate', 'Overuse of certain laxatives including magnesium hydroxide may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation. Coadministration with aluminum- or magnesium-containing antacids may decrease the serum concentrations of orally administered phenothiazines. The proposed mechanism is antacid adsorption resulting in reduced phenothiazine bioavailability. The interaction has been reported for chlorpromazine but may occur with other phenothiazines.', 'DDInter', 1767317477, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives including magnesium hydroxide. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Absorption, Others', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives including magnesium hydroxide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
