INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516048, 'Sibutramine', 'Mazindol', 'Major', 'Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake. As such, it may potentiate the pharmacologic response to sympathomimetic agents due to additive or synergistic effects. Although the interaction has not been studied with sibutramine, it has been reported with the selective serotonin reuptake inhibitor, fluoxetine.', 'DDInter', 1767317477, 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', 'Synergism', 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516049, 'Sibutramine', 'Mephentermine', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when sibutramine is combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake.', 'DDInter', 1767317477, 'Caution is advised if sibutramine is coadministered with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating therapy with sibutramine and monitored at regular intervals thereafter. Dose reduction or discontinuation of sibutramine should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', 'Caution is advised if sibutramine is coadministered with other drugs that can increase blood pressure and/or heart rate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516050, 'Sibutramine', 'Mephenytoin', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
