INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516102, 'Bepridil', 'Mephenytoin', 'Moderate', 'Diltiazem and verapamil may increase plasma phenytoin levels. Toxicity has been reported. The proposed mechanism is inhibition of CYP450 3A4 metabolism. In addition, phenytoin may significantly decrease calcium channel blocker (CCB) serum levels by inducing first-pass metabolism and the systemic clearance. Other hydantoins may participate in these interactions as well.', 'DDInter', 1767317477, 'Close clinical and laboratory observation for evidence of altered effect of both drugs is recommended if a CCB and a hydantoin must be used together. Patients should be advised to notify their physician if they experience symptoms of phenytoin toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia) or loss of effect of their CCB.', 'Metabolism', 'Close clinical and laboratory observation for evidence of altered effect of both drugs is recommended if a CCB and a hydantoin must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
