INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516154, 'Ranitidine (bismuth citrate)', 'Mecobalamin', 'Minor', 'By reducing or suppressing gastric acid secretion, H2-receptor antagonists and proton pump inhibitors may interfere with the gastrointestinal absorption of vitamin B12, a process that is dependent on the presence of gastric acid and pepsin. Clinical studies have shown that dietary (i.e., protein-bound) vitamin B12 malabsorption can occur during treatment with these agents, particularly proton pump inhibitors, although the likelihood of developing clinically significant deficiency over time is unknown.', 'DDInter', 1767317477, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516155, 'Acetohexamide', 'Methyldopa', 'Minor', 'Methyldopa may inhibit the metabolism of some antidiabetic agents. The serum half-life and the effectiveness of those agents may be increased, and unexpected hypoglycemia could result. When methyldopa is added to or deleted from a medical regimen that includes antidiabetic agents, the patient should be made aware of this interaction. Blood glucose should be checked more frequently, if necessary.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516156, 'Methyldopa', 'Mesoridazine', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317477, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516157, 'Phenobarbital', 'Methyldopa', 'Minor', 'Some barbiturates may increase the plasma clearance of methyldopa. Antihypertensive effectiveness may be decreased. Data are available for phenobarbital only and are conflicting. The clinical relevance of this interaction is minimal.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
