INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516160, 'Terbutaline', 'Methyldopa', 'Moderate', 'Limited data suggest that methyldopa and other sympatholytics may increase or prolong the pressor effect of sympathomimetics. Hypertension may result. Data exist for phenylpropanolamine and norepinephrine.', 'DDInter', 1767317477, 'If these drugs must be coadministered, blood pressure should be closely monitored. Discontinuation of the sympathomimetic may be necessary if hypertension develops.', 'Synergism', 'If these drugs must be coadministered, blood pressure should be closely monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
