INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516278, 'Doxacurium', 'Quinidine', 'Moderate', 'The pharmacologic effects of nondepolarizing muscle relaxants and quinidine may be additive. The mechanism may be related to depression of the action potential in skeletal muscles and curare-like effects on the neuromuscular junction by quinidine. This combination could result in increased neuromuscular blockade and respiratory depression.', 'DDInter', 1767317477, 'Quinidine should not be administered immediately after surgery. If it is necessary, the patient''s vital signs should be closely monitored. Mechanical ventilatory support may be required during coadministration.', 'Synergism', 'Quinidine should not be administered immediately after surgery.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516279, 'Doxacurium', 'Ranitidine (bismuth citrate)', 'Minor', 'Ranitidine may or may not decrease the effectiveness of neuromuscular blocking agents. Until more information is available, the clinician should be aware of the possibility of an interaction.', 'DDInter', 1767317477, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
