        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1516294, 'Estrone sulfate (topical)', 'Metyrapone', 'Moderate', 'Estrogen therapy may reduce the response to metyrapone test. The mechanism is related to an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in decreased cortisol clearance. The increase in circulating cortisol may directly interfere with test results, since the pharmacologic effect of metyrapone is to inhibit adrenal production of cortisol and corticosterone in order to elicit an increase in ACTH production by the pituitary.', NULL, 'Clinicians should recognize the potential for a diminished response to metyrapone test in patients treated with estrogens.', NULL, 'Clinicians should recognize the potential for a diminished response to metyrapone test in patients treated with estrogens.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
