        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1516421, 'Bepridil', 'Paclitaxel (protein-bound)', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the blood concentrations of the taxanes, docetaxel and paclitaxel, which have been shown to be substrates of the efflux transporter.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of paclitaxel or docetaxel therapy should be considered during coadministration with P-gp inhibitors, including adverse effects such as myelosuppression, stomatitis, neurotoxicity (e.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of paclitaxel or docetaxel therapy should be considered during coadministration with P-gp inhibitors, including adverse effects such as myelosuppression, stomatitis, neurotoxicity (e.g., paraesthesia, dysesthesia, pain), myalgia, asthenia, fluid retention, nausea, vomiting, and diarrhea. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a P-gp inhibitor is added to or withdrawn from therapy.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
