    ) VALUES (1516516, 'Digitoxin', 'Pectin', 'Moderate', 'INTERVAL: Concurrent administration of kaolin-pectin may decrease the oral bioavailability of digitalis glycosides. The mechanism of interaction is unknown.', NULL, 'To minimize the potential for interaction, patients should be advised to take kaolin-pectin at least 2 hours after digoxin if possible.', NULL, 'To minimize the potential for interaction, patients should be advised to take kaolin-pectin at least 2 hours after digoxin if possible. The same precaution may be applicable during therapy with digitoxin, although clinical data are lacking.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
