INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516607, 'Caspofungin', 'Phenobarbital', 'Moderate', 'Coadministration with inducers or mixed inducer/inhibitors of certain metabolic pathways may produce clinically meaningful reductions in the plasma concentrations of caspofungin.', 'DDInter', 1767317477, 'Caspofungin product labeling recommends that during coadministration with efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone or carbamazepine, an increase in the daily dosage to 70 mg (following the usual 70 mg loading dose) should be considered in patients who are not clinically responding.', 'Metabolism', 'Caspofungin product labeling recommends that during coadministration with efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone or carbamazepine, an increase in the daily dosage to 70 mg (following the usual 70 mg loading dose) should be considered in patients who are not clinically responding.', NULL, NULL, 'Moderate', 'pharmacodynamic');
