INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516622, 'St. John''s Wort', 'Phenobarbital', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516623, 'Tetracaine (topical)', 'Phenobarbital', 'Moderate', 'Some topical anesthetics can be systemically absorbed and cause methemoglobinemia, particularly when applied to mucous membranes. Coadministration with other oxidizing agents that can also induce methemoglobinemia such as injectable local anesthetics, antimalarials (e.g., chloroquine, primaquine, quinine, tafenoquine), nitrates and nitrites, sulfonamides, acetaminophen, aminosalicylic acid, dapsone, dimethyl sulfoxide, flutamide, metoclopramide, nitrofurantoin, phenazopyridine, phenobarbital, phenytoin, and rasburicase may increase the risk. Additional risk factors include very young age (e.g., infants less than 6 months); application to inflamed/abraded areas or broken skin; anemia; cardiac or pulmonary disease; peripheral vascular disease; liver cirrhosis; shock; sepsis; acidosis; and genetic predisposition (e.g., NADH cytochrome-b5 reductase deficiency; glucose-6-phosphate dehydrogenase (G6PD) deficiency; hemoglobin M).', 'DDInter', 1767317477, 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents. Clinicians should be aware of the potential for methemoglobinemia, particularly when topical anesthetics are applied to mucous membranes or given via the oropharyngeal route. Patients or their caregivers should be advised to seek medical attention if they notice signs and symptoms of methemoglobinemia such as slate-grey cyanosis in buccal mucous membranes, lips, and nail beds; nausea; headache; dizziness; lightheadedness; lethargy; fatigue; dyspnea; tachypnea; tachycardia; palpitation; anxiety; and confusion.', 'Synergism', 'Caution is advised when topical anesthetics are used concomitantly with other methemoglobin-inducing agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
