INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516625, 'Tivozanib', 'Phenobarbital', 'Major', 'Coadministration of tivozanib with strong CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of tivozanib, which has been shown to be a substrate of the isoenzyme.', 'DDInter', 1767317477, 'Due to the potential for reduced anti-tumor activity, concomitant use of tivozanib with a strong CYP450 3A4 inducer should generally be avoided. If coadministration is necessary, some authorities advise caution, with clinical and laboratory monitoring to be considered whenever a strong CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Due to the potential for reduced anti-tumor activity, concomitant use of tivozanib with a strong CYP450 3A4 inducer should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
