INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516627, 'Valdecoxib', 'Phenobarbital', 'Moderate', 'Coadministration with phenytoin and/or other enzyme-inducing anticonvulsants may decrease the plasma concentrations of valdecoxib. The mechanism is induction of CYP450 2C9 and 3A4, the isoenzymes responsible for the metabolic clearance of valdecoxib.', 'DDInter', 1767317477, 'Patients already stabilized on valdecoxib may experience loss of pain and inflammation control with the coadministration of phenytoin and/or other enzyme-inducing anticonvulsants such as carbamazepine, oxcarbazepine, phenobarbital, and primidone. Pharmacologic response to valdecoxib should be monitored more closely whenever these agents are added to or withdrawn from therapy, and the valdecoxib dosage adjusted as necessary. In addition, because valdecoxib is a moderate inhibitor of CYP450 2C9 and 2C19 and a weak inhibitor of 2D6 and 3A4, valdecoxib labeling recommends that routine monitoring for alteration of antiepileptic efficacy be performed when therapy with valdecoxib is either initiated or discontinued in patients receiving anticonvulsants.', 'Metabolism', 'Patients already stabilized on valdecoxib may experience loss of pain and inflammation control with the coadministration of phenytoin and/or other enzyme-inducing anticonvulsants such as carbamazepine, oxcarbazepine, phenobarbital, and primidone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
