INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516672, 'Polymyxin B', 'Metocurine', 'Major', 'Polymyxin B may prolong apnea and respiratory paralysis after use of neuromuscular blocking agents. The mechanism may be related to decreased intracellular potassium or decreased ionized serum calcium. Intravenous calcium administration may be helpful in reversing the paralysis.', 'DDInter', 1767317477, 'This combination should be avoided unless the benefit outweighs the risk. Patients should be closely monitored for signs of extended neuromuscular blockade.', 'Others', 'This combination should be avoided unless the benefit outweighs the risk.', NULL, NULL, 'Major', 'pharmacodynamic');
