INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516752, 'Digitoxin', 'Propylthiouracil', 'Moderate', 'The clearance of digitalis glycosides may be reduced when a euthyroid state is achieved after the addition of antithyroid agents.', 'DDInter', 1767317477, 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when antithyroid agents are added, discontinued, or dosing changes are made. Digitalis dose reductions may be required.', 'Excretion', 'Monitor the patient closely for altered efficacy and safety while achieving a euthyroid state or when antithyroid agents are added, discontinued, or dosing changes are made.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516753, 'Propylthiouracil', 'Oxtriphylline', 'Moderate', 'Clearance of theophylline and related agents depends upon thyroid function. In the hyperthyroid state, clearance may be enhanced. In the hypothyroid state, clearance may be reduced. Theophylline clearance may decrease and the risk of toxicity may increase when hyperthyroid patients become euthyroid.', 'DDInter', 1767317477, 'The patient and theophylline levels should be closely monitored while achieving a euthyroid state and when an antithyroid agent is added, discontinued, or the dosage is changed. Theophylline dosage adjustments may be required. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms, or any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats.', 'Others', 'The patient and theophylline levels should be closely monitored while achieving a euthyroid state and when an antithyroid agent is added, discontinued, or the dosage is changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516754, 'Propylthiouracil', 'Penbutolol', 'Moderate', 'The clearance of some beta-blockers with high extraction ratios may be reduced when a euthyroid state is achieved after the addition of antithyroid agents. Sotalol, nadolol, carteolol, and atenolol, which are primarily eliminated by the kidney, do not appear to be affected by changes in thyroid function.', 'DDInter', 1767317477, 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed. Beta blocker dose reductions may be required.', 'Others', 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516755, 'Abarelix', 'Capromab pendetide', 'Moderate', 'The effect of surgical and/or medical androgen ablation on the imaging performance of Indium In 111 capromab pendetide has not been evaluated. Preliminary data suggest that hormone ablation may increase expression of Prostate Specific Membrane Antigen (PSMA)--the target of capromab pendetide--and decrease tumor expression of prostate specific antigen (PSA).', 'DDInter', 1767317477, 'Until more data are available, concomitant use of Indium In 111 capromab pendetide in patients treated with androgen ablation therapy is not recommended.', 'Others', 'Until more data are available, concomitant use of Indium In 111 capromab pendetide in patients treated with androgen ablation therapy is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1516756, 'Acetohexamide', 'Plantago seed', 'Minor', 'Large doses of soluble dietary fibers such as guar gum, psyllium, or methylcellulose may add to the hypoglycemic effect of insulin and oral antidiabetic agents. Soluble fibers slow the rate of carbohydrate degradation and glucose absorption during digestion, thus they can reduce postprandial blood glucose when taken in large amounts. Soluble dietary fibers are often considered beneficial as an adjunct to insulin and oral antidiabetic agents in the management of diabetes. However, patients should be monitored for changes in diabetic medication requirements following initiation of fiber therapy.', 'DDInter', 1767317477, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
