INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1517044, 'Guanadrel', 'Mesoridazine', 'Moderate', 'Phenothiazines may inhibit the antihypertensive effect of guanethidine, probably through inhibition of guanethidine neuronal uptake. No data are available for guanadrel, but a similar interaction is suspected. Other adrenergic neurone blockers may also interact.', 'DDInter', 1767317477, 'This combination should generally be avoided. An alternative antihypertensive regimen may be necessary for patients who must take phenothiazine antipsychotics. If patients do receive a phenothiazine with an adrenergic neurone blocker, their blood pressure should be monitored closely. The dosage of the antihypertensive may require adjustment.', 'Antagonism', 'This combination should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
