INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1517070, 'Phenylpropanolamine', 'Mesoridazine', 'Moderate', 'Phenothiazines may antagonize the pharmacologic effects of amphetamine, amphetamine derivatives, and other centrally-acting sympathomimetic agents (i.e., CNS stimulants). Conversely, these agents may diminish the neuroleptic efficacy of phenothiazines. The exact mechanism of interaction is unknown but may involve opposing effects on dopaminergic activity.', 'DDInter', 1767317477, 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with phenothiazines.', 'Antagonism', 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with phenothiazines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1517071, 'Physostigmine (ophthalmic)', 'Mesoridazine', 'Moderate', 'Theoretically, anticholinergic agents and other agents with significant anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide) may antagonize the effects of topically administered cholinergic agents such as acetylcholine, carbachol, demecarium, echothiophate, isoflurophate, physostigmine, and pilocarpine. The proposed mechanism involves opposing pharmacodynamic action on muscarinic receptor sites in ocular tissue. This interaction is sometimes desirable and is the basis for using atropine in the treatment of excessive muscarinic side effects and cholinergic crisis induced by cholinergic overdose.', 'DDInter', 1767317477, 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', 'Antagonism', 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1517072, 'Probucol', 'Mesoridazine', 'Major', 'Mesoridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Mesoridazine overdosage has been associated with ventricular arrhythmias and death. The extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with mesoridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 1767317477, 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
