INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1517164, 'St. John''s Wort', 'Quinestrol', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1517165, 'St. John''s Wort', 'Relugolix', 'Major', 'Coadministration with potent inducers of CYP450 3A4 that also induce P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of relugolix. In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8. Relugolix is also a substrate for intestinal P-gp.', 'DDInter', 1767317477, 'Concomitant use of relugolix with combined P-gp and potent CYP450 3A inducers should generally be avoided. If coadministration is required, the manufacturer recommends increasing the relugolix dosage to 240 mg once daily. Therapeutic response and tolerance should be monitored more frequently. Following discontinuation of the P-gp/CYP450 3A inducer, treatment with relugolix should be resumed at the normally recommended dosage of 120 mg once daily.', 'Metabolism', 'Concomitant use of relugolix with combined P-gp and potent CYP450 3A inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1517166, 'Telaprevir', 'St. John''s Wort', 'Major', 'Coadministration with St. John''s wort may significantly decrease the plasma concentrations of telaprevir, a hepatitis C virus NS3/4A protease inhibitor. The proposed mechanism involves induction of telaprevir metabolism via CYP450 3A4.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of telaprevir with St. John''s wort is considered contraindicated. Patients should be advised to consult with their caregivers before using any herbal or alternative medicines.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of telaprevir with St.', NULL, NULL, 'Major', 'pharmacodynamic');
