INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1517169, 'Tivozanib', 'St. John''s Wort', 'Major', 'Coadministration of tivozanib with strong CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of tivozanib, which has been shown to be a substrate of the isoenzyme. The clinical effects of strong CYP450 3A4 inducers on repeated daily dosing of tivozanib has not been studied.', 'DDInter', 1767317477, 'Due to the potential for reduced anti-tumor activity, concomitant use of tivozanib with a strong CYP450 3A4 inducer such as St. John''s wort should be generally avoided. Some authorities consider the concomitant use of tivozanib with St. John''s wort to be contraindicated. It should be noted that the inducing effect of St. John''s wort might persist for at least 2 weeks following discontinuation of treatment.', 'Metabolism', 'Due to the potential for reduced anti-tumor activity, concomitant use of tivozanib with a strong CYP450 3A4 inducer such as St.', NULL, NULL, 'Major', 'pharmacodynamic');
