INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1517292, 'Berotralstat', 'Thioridazine', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may increase the plasma concentrations of thioridazine and its two active metabolites, mesoridazine and sulforidazine, all of which are substrates of the isoenzyme. The use of thioridazine has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', 'DDInter', 1767317477, 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated. Depending on the elimination half-life of these drugs, a considerable waiting period may be appropriate following their discontinuation before thioridazine is initiated. In addition, the prolonged duration of CYP450 2D6 inhibition by the moderate CYP450 2D6 inhibitor rolapitant of at least 28 days after its administration should also be taken into account.', 'Metabolism', 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
