INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1517420, 'Tolbutamide', 'Ranitidine (bismuth citrate)', 'Moderate', 'H2 antagonists such as cimetidine and ranitidine may increase plasma concentrations of sulfonylureas and enhance hypoglycemic effects. The mechanism may be related to inhibition of liver cytochrome P450 enzymes responsible for sulfonylurea metabolism or increased absorption due to altered gastric pH. Cimetidine may also inhibit glucose metabolism. Other H2 antagonists may interact in a similar manner, particularly if increased pH is involved.', 'DDInter', 1767317477, 'Clinical monitoring of patient response, tolerance, and glycemic control is recommended. Patients receiving this combination should be advised to regularly monitor their blood sugar, counseled on how to recognize and treat hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, tremor, hunger, weakness, or palpitations) and to notify their physician if it occurs. The sulfonylurea dosage may require reduction in affected patients.', 'Absorption', 'Clinical monitoring of patient response, tolerance, and glycemic control is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
