    ) VALUES (1517619, 'Vecuronium', 'Spectinomycin', 'Moderate', 'Some drugs may enhance the effects of neuromuscular blocking agents.', NULL, 'Observation for profound and prolonged respiratory depression and muscle weakness is recommended if these drugs must be used together.', NULL, 'Observation for profound and prolonged respiratory depression and muscle weakness is recommended if these drugs must be used together.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
