    ) VALUES (1560555, 'Ospemifene', 'Medroxyprogesterone acetate', 'Moderate', 'The concurrent use of ospemifene with estrogens or other estrogen agonists/antagonists has not been evaluated.', NULL, 'Due to the lack of clinical data, concomitant use of ospemifene with estrogens or other estrogen agonists/antagonists is not recommended.', NULL, 'Due to the lack of clinical data, concomitant use of ospemifene with estrogens or other estrogen agonists/antagonists is not recommended.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
