    ) VALUES (1560563, 'Selumetinib', 'Medroxyprogesterone acetate', 'Moderate', 'Coadministration with selumetinib may reduce the effectiveness of hormonal contraceptives.', NULL, 'Until further data are available, females of reproductive potential using hormonal contraceptives should be advised to add a barrier method during treatment with selumetinib and for 1 week after the final dose.', NULL, 'Until further data are available, females of reproductive potential using hormonal contraceptives should be advised to add a barrier method during treatment with selumetinib and for 1 week after the final dose.  In addition, men with female partners of reproductive potential should also use effective contraception during treatment with selumetinib and for 1 week after the last dose.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
