    ) VALUES (1560605, 'Dexmethylphenidate', 'Nifedipine', 'Moderate', 'Methylphenidate and other derivative amphetamines may decrease the therapeutic effects of antihypertensive drugs.', NULL, 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.', NULL, 'Caution and monitoring of blood pressure is recommended if methylphenidate, dexmethylphenidate or serdexmethylphenidate are administered with antihypertensives agents such as:  potassium-sparing and thiazide diuretics, calcium channel blockers, angiotensin-converting-enzyme (ACE) inhibitors, angiotensin II receptor blockers (ARBs), beta blockers or centrally acting alpha-2 receptor agonists.  The dosage of the antihypertensive drug should be adjusted as needed.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
