    ) VALUES (1560623, 'Brentuximab vedotin', 'Selinexor', 'Moderate', 'Brentuximab vedotin can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', NULL, 'The potential for increased risk and/or severity of peripheral neuropathy should be considered when brentuximab vedotin is used with other drugs that are also associated with peripheral neuropathy.', NULL, 'The potential for increased risk and/or severity of peripheral neuropathy should be considered when brentuximab vedotin is used with other drugs that are also associated with peripheral neuropathy.  Patients should be closely monitored for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness.  Patients experiencing new, worsening, or recurrent neuropathy may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
