    ) VALUES (1560627, 'Tadalafil', 'Avanafil', 'Major', 'The safety and efficacy of combining a phosphodiesterase-5 (PDE5) inhibitor with other PDE-5 inhibitors or other treatments for erectile dysfunction have not been studied.  Since these agents have systemic vasodilatory properties, concurrent use may result in additive hypotensive and other cardiovascular adverse effects.', NULL, 'Concomitant use of a PDE5 inhibitor with another PDE5 inhibitor should generally be avoided.', NULL, 'Concomitant use of a PDE5 inhibitor with another PDE5 inhibitor should generally be avoided.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
