INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560700, 'Adefovir dipivoxil', 'Oxaliplatin', 'Moderate', 'Theoretically, coadministration with drugs that are nephrotoxic may delay and/or decrease the clearance of oxaliplatin, which is primarily eliminated unchanged by the kidney.', 'DDInter', 1767317477, 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents).  The potential for increased toxicity of oxaliplatin such as peripheral sensory neuropathies and neutropenia should be considered.  Renal function should be closely monitored during therapy.', 'Excretion', 'Caution is advised if oxaliplatin is used in patients who have recently received or are receiving treatment with potentially nephrotoxic drugs (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
