INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560709, 'Adefovir dipivoxil', 'Procainamide', 'Moderate', 'Theoretically, coadministration of adefovir dipivoxil with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of adefovir and/or the coadministered drug(s).  The mechanism is competitive inhibition of renal excretion.  Drugs that are thought to undergo active tubular secretion include metformin, cimetidine, ranitidine, procainamide, flecainide, quinidine, triamterene, midodrine, cidofovir, acyclovir, valacyclovir, tenofovir, ganciclovir, and valganciclovir.', 'DDInter', 1767317477, 'Patients receiving adefovir dipivoxil in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', 'Patients receiving adefovir dipivoxil in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560710, 'Adefovir dipivoxil', 'Quinidine', 'Moderate', 'Theoretically, coadministration of adefovir dipivoxil with other drugs that are eliminated by active tubular secretion may result in increased plasma concentrations of adefovir and/or the coadministered drug(s).  The mechanism is competitive inhibition of renal excretion.  Drugs that are thought to undergo active tubular secretion include metformin, cimetidine, ranitidine, procainamide, flecainide, quinidine, triamterene, midodrine, cidofovir, acyclovir, valacyclovir, tenofovir, ganciclovir, and valganciclovir.', 'DDInter', 1767317477, 'Patients receiving adefovir dipivoxil in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', 'Excretion', 'Patients receiving adefovir dipivoxil in combination with other drugs that undergo active tubular secretion should be monitored for excessive pharmacologic effects of one or both drugs, and the dosages of the drugs adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
