INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560839, 'Bupropion', 'Oxymetazoline (nasal)', 'Moderate', 'Translated with DeepL', 'DDInter', 1767317477, 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.  Blood pressure and heart rate should be measured prior to initiating bupropion therapy and monitored at regular intervals consistent with usual clinical practice, particularly in patients with preexisting hypertension.  Dose reduction or discontinuation of bupropion should be considered in patients who experience clinically significant and sustained increases in blood pressure or heart rate.', 'Synergism', 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560840, 'Bupropion', 'Phenylephrine (nasal)', 'Moderate', 'Translated with DeepL', 'DDInter', 1767317477, 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.  Blood pressure and heart rate should be measured prior to initiating bupropion therapy and monitored at regular intervals consistent with usual clinical practice, particularly in patients with preexisting hypertension.  Dose reduction or discontinuation of bupropion should be considered in patients who experience clinically significant and sustained increases in blood pressure or heart rate.', 'Synergism', 'Caution is advised when bupropion is used with other drugs that increase dopaminergic or noradrenergic activity due to an increased risk of hypertension.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560841, 'Osilodrostat', 'Levonorgestrel', 'Moderate', 'Coadministration with osilodrostat may increase the plasma concentrations of drugs that are metabolized by CYP450 1A2, 2C19, 2D6, and/or 3A4.  Osilodrostat has been shown to be a moderate inhibitor of CYP450 1A2, a mild to borderline moderate inhibitor of CYP450 2C19, and a weak inhibitor of CYP450 2D6 and 3A4.', 'DDInter', 1767317477, 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osilodrostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560842, 'Selumetinib', 'Levonorgestrel', 'Moderate', 'Coadministration with selumetinib may reduce the effectiveness of hormonal contraceptives.', 'DDInter', 1767317477, 'Until further data are available, females of reproductive potential using hormonal contraceptives should be advised to add a barrier method during treatment with selumetinib and for 1 week after the final dose.  In addition, men with female partners of reproductive potential should also use effective contraception during treatment with selumetinib and for 1 week after the last dose.', 'Antagonism', 'Until further data are available, females of reproductive potential using hormonal contraceptives should be advised to add a barrier method during treatment with selumetinib and for 1 week after the final dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560843, 'Tivozanib', 'Levonorgestrel', 'Moderate', 'Coadministration with tivozanib may decrease the efficacy of contraceptive hormones.', 'DDInter', 1767317477, 'Until more information is known, caution is advised when tivozanib is prescribed concomitantly with hormonal contraceptives.  Some authorities recommend that female patients of childbearing potential use an additional method of contraception.  An acceptable barrier method (e.g., diaphragm with spermicide, cervical cap with spermicide, contraceptive sponge, male condom, female condom) in addition to the hormonal contraceptive of choice may be advisable during the duration of tivozanib therapy and for at least one month after completing therapy.', 'Antagonism', 'Until more information is known, caution is advised when tivozanib is prescribed concomitantly with hormonal contraceptives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
