INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560847, 'Anagrelide', 'Pralsetinib', 'Moderate', 'Pralsetinib can cause prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Some authorities recommend caution if pralsetinib is coadministered with other agents known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560848, 'Anagrelide', 'Relugolix', 'Major', 'Anagrelide can cause dose-dependent prolongation of the QT interval.  Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of anagrelide with other drugs that can prolong the QT interval should generally be avoided.  Patients receiving anagrelide should have a cardiovascular examination, including an ECG, prior to initiation of treatment and monitored for cardiovascular effects during treatment.  Hypokalemia or hypomagnesemia must be corrected prior to therapy and electrolytes monitored periodically during therapy.  Anagrelide should not be used in the presence of known risk factors for QT interval prolongation such as congenital long QT syndrome or a history of acquired QTc prolongation.  Periodic electrocardiograms are recommended in patients with heart failure, bradyarrhythmias, or electrolyte abnormalities.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of anagrelide with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560849, 'Anagrelide', 'Turmeric', 'Moderate', 'Turmeric may potentiate the bleeding risk associated with drugs that interfere with platelet function or coagulation.', 'DDInter', 1767317477, 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.  Clinical and laboratory observation for hematologic complications is recommended.', 'Synergism', 'Caution is advised when turmeric-containing products are used concomitantly with drugs that affect hemostasis such as anticoagulants, antiplatelet agents, nonsteroidal anti-inflammatory drugs, serotonin reuptake inhibitors, and thrombolytic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
