INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560883, 'Osilodrostat', 'Loratadine', 'Moderate', 'Coadministration with osilodrostat may increase the plasma concentrations of drugs that are metabolized by CYP450 1A2, 2C19, 2D6, and/or 3A4.  Osilodrostat has been shown to be a moderate inhibitor of CYP450 1A2, a mild to borderline moderate inhibitor of CYP450 2C19, and a weak inhibitor of CYP450 2D6 and 3A4.', 'DDInter', 1767317477, 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever osilodrostat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when osilodrostat is used concurrently with drugs that are substrates of CYP450 1A2, 2C19, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560884, 'Pralsetinib', 'Loratadine', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 1767317477, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560885, 'Albendazole', 'Berotralstat', 'Moderate', 'Coadministration with berotralstat may increase the plasma concentrations and effects of drugs that are substrates of CYP450 3A4 and/or 2D6.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 and 2D6 activity by berotralstat.', 'DDInter', 1767317477, 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.  Clinical and laboratory monitoring are recommended following the initiation of berotralstat, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when berotralstat is coadministered with drugs that are substrates of CYP450 3A4 and/or 2D6, particularly those with a narrow therapeutic index including but not limited to thioridazine, pimozide, cyclosporine, and fentanyl.', NULL, NULL, 'Moderate', 'pharmacodynamic');
