INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1560897, 'Aldesleukin', 'Scopolamine', 'Moderate', 'Sedatives, tranquilizers, muscle relaxants, antidepressants, and other central nervous system (CNS) depressants may have additive CNS-depressant effects with aldesleukin.', 'DDInter', 1767317477, 'Caution is advised if aldesleukin is used with sedatives, hypnotics, tranquilizers, or other CNS-active medications.  Patients should be monitored for CNS depression and other CNS adverse effects.  Treatment with aldesleukin should be discontinued if severe lethargy or somnolence develop.', 'Synergism', 'Caution is advised if aldesleukin is used with sedatives, hypnotics, tranquilizers, or other CNS-active medications.', NULL, NULL, 'Moderate', 'pharmacodynamic');
