        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1561037, 'Fexofenadine', 'Relugolix', 'Moderate', 'Concomitant use with relugolix may lead to increased plasma concentrations of substrates of the intestinal efflux transporter, P-glycoprotein (P-gp).  According to the manufacturer, a relugolix dose of 40 mg may lead to saturation of intestinal P-gp.  In addition, relugolix exhibits more than dose-proportional pharmacokinetics over a dose range of 10 mg to 120 mg, which may lead to increased absorption of coadministered medicines that are sensitive substrates of P-gp.', NULL, 'Some authorities advise that the coadministration of relugolix with sensitive substrates of P-glycoprotein, such as dabigatran etexilate, should be avoided.', NULL, 'Some authorities advise that the coadministration of relugolix with sensitive substrates of P-glycoprotein, such as dabigatran etexilate, should be avoided.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
