        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1561136, 'Hydralazine', 'Ramipril', 'Moderate', 'Concomitant treatment with other antihypertensive agents or vasodilators, including alpha-adrenoreceptor antagonists, angiotensin converting enzyme (ACE) inhibitors, angiotensin receptor blockers (ARBs), beta-adrenergic blockers, calcium channel blockers, diuretics and nitrates, may potentiate the hypotensive effects of hydralazine and dihydralazine.', NULL, 'Blood pressure and heart rate should be closely monitored when hydralazine or dihydralazine is used with other agents that can induce hypotension.', NULL, 'Blood pressure and heart rate should be closely monitored when hydralazine or dihydralazine is used with other agents that can induce hypotension.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
