    ) VALUES (1561315, 'Ulipristal', 'Pazopanib', 'Minor', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of ulipristal, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of ulipristal, which is primarily metabolized by the isoenzyme.', NULL, 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of ulipristal, which is primarily metabolized by the isoenzyme.  The clinical relevance is unknown.', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
