        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1561480, 'Rivaroxaban', 'Levetiracetam', 'Moderate', 'Data from a nested case-control study suggest that coadministration with levetiracetam or valproic acid may reduce the therapeutic effects of the direct-acting oral anticoagulants (DOACs) apixaban, dabigatran, and rivaroxaban.', NULL, 'The potential for diminished pharmacologic effects of apixaban, dabigatran, and rivaroxaban should be considered during coadministration with levetiracetam or valproic acid.', NULL, 'The potential for diminished pharmacologic effects of apixaban, dabigatran, and rivaroxaban should be considered during coadministration with levetiracetam or valproic acid.  Alternative treatments or dosage adjustments may be required if an interaction is suspected.  Some experts recommend avoiding the concomitant use of DOACs with valproic acid.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
