    ) VALUES (1561502, 'Telotristat ethyl', 'Perampanel', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of perampanel, which is primarily metabolized by the isoenzyme.', NULL, 'According to the manufacturer, concomitant use of perampanel with strong CYP450 3A4 inducers should be avoided.', NULL, 'According to the manufacturer, concomitant use of perampanel with strong CYP450 3A4 inducers should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
