        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1561529, 'Sodium oxybate', 'Scopolamine', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of other agents with CNS depressant effects.', NULL, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.', NULL, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.  Otherwise, close monitoring and/or dosage reductions should be considered.  If short-term use of a CNS depressant is required (e.g., post- or perioperative opioid), a temporary interruption of sodium oxybate therapy may be appropriate.  All patients treated with sodium oxybate should be advised not to drive, operate machinery, or engage in potentially hazardous activities requiring mental alertness and motor coordination for at least 6 hours after taking the second nightly dose of sodium oxybate and until they know how the medication affects them.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
