INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561603, 'Selpercatinib', 'Niraparib', 'Moderate', 'Coadministration with selpercatinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter.  The proposed mechanism is decreased clearance in the intestine, kidney, and/or liver due to inhibition of P-gp-mediated efflux by selpercatinib.', 'DDInter', 1767317477, 'Caution is advised when selpercatinib is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selpercatinib is added to or withdrawn from therapy.', 'Metabolism, Absorption, Excretion', 'Caution is advised when selpercatinib is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
