INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561613, 'Bacillus calmette-guerin substrain tice live antigen', 'Ozanimod', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during ozanimod therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence.  Ozanimod causes reversible sequestration of lymphocytes in lymphoid tissues.', 'DDInter', 1767317477, 'The use of live attenuated vaccines should be avoided during and for up to 3 months after discontinuing ozanimod treatment.  If live attenuated vaccine immunizations are required, administration should occur at least 1 month prior to initiation of ozanimod.  Patients without a healthcare professional confirmed history of varicella or without documentation of a full course of vaccination against varicella-zoster virus (VZV) should be tested for antibodies to VZV before initiating treatment with ozanimod.  For antibody-negative patients, a full course of vaccination with varicella vaccine is recommended prior to treatment with ozanimod.  In patients who have recently been vaccinated, ozanimod therapy should be postponed for 4 weeks to allow the full effect of vaccination to occur.', 'Synergism', 'The use of live attenuated vaccines should be avoided during and for up to 3 months after discontinuing ozanimod treatment.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561614, 'Bisacodyl', 'Ozanimod', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval.', 'DDInter', 1767317477, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.  The recommended dosage and duration of use should not be exceeded.  Patients treated with lactulose for more than six months should be monitored periodically for electrolyte imbalance.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
