INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561617, 'Denosumab', 'Ozanimod', 'Moderate', 'Concomitant use of immunosuppressive or myelosuppressive agents with denosumab may increase the risk of serious infections.', 'DDInter', 1767317477, 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents.  Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination.  The need for continued denosumab therapy should be assessed when serious infections occur during treatment.', 'Synergism', 'Caution is advised if denosumab must be used in combination with immuno- or myelosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561618, 'Eslicarbazepine', 'Ozanimod', 'Moderate', 'The risk of transient bradycardia and atrioventricular (AV) block may be increased during initiation of ozanimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis.', 'DDInter', 1767317477, 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.  Since initiation of ozanimod may result in a transient decrease in heart rate and AV conduction delays, the manufacturer''s product labeling should be consulted for an up-titration scheme to reach the maintenance dosage of ozanimod.  This may attenuate the magnitude of heart rate reductions.  In addition, advice from a cardiologist should be sought if coadministration of ozanimod is considered in patients with significant QT prolongation (QTcF greater than 450 msec in males or 470 msec in females), patients with arrhythmias requiring treatment with Class 1a or Class III antiarrhythmic agents, patients with ischemic heart disease, heart failure, history of cardiac arrest or myocardial infarction, cerebrovascular disease, or uncontrolled hypertension.  Use of ozanimod in patients with a history of second-degree Mobitz type II or higher AV block, sick-sinus syndrome, or sinoatrial heart block is considered contraindicated unless the patient has a functioning pacemaker.', 'Synergism', 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
