INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561783, 'Amobarbital', 'Scopolamine', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.  Cautious dosage titration may be required, particularly at treatment initiation.  Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561784, 'Scopolamine', 'Apomorphine', 'Moderate', 'Central nervous system (CNS) depressant effects may be additively or synergistically increased in patients using apomorphine in combination with other drugs that can also cause these effects.', 'DDInter', 1767317477, 'The use of other sedating drugs should generally be avoided during apomorphine treatment.  Patients prescribed these agents concurrently should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated.  Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them.  If patients experience increased episodes of falling asleep during normal daily activities, they should avoid driving and other potentially hazardous activities until they have contacted their physician.', 'Synergism', 'The use of other sedating drugs should generally be avoided during apomorphine treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
