INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561890, 'Selinexor', 'Secnidazole', 'Moderate', 'The risk of peripheral neuropathy may be increased during concurrent use of two or more agents that are associated with this adverse effect.', 'DDInter', 1767317477, 'Caution is advised during concomitant use of agents with neurotoxic effects.  Patients should be monitored closely for symptoms of neuropathy such as burning, tingling, pain, or numbness in the hands and feet.  Since the development of peripheral neuropathy may be dose-related for many drugs, the recommended dosages should generally not be exceeded.  Consideration should be given to dosage reduction or immediate discontinuation of these medications in patients who develop peripheral neuropathy to limit further damage.  If feasible, therapy should generally be reinstituted only after resolution of neuropathy symptoms or return of symptoms to baseline status.  In some cases, permanent dosage reductions may be required.', 'Synergism', 'Caution is advised during concomitant use of agents with neurotoxic effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
