INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561908, 'Terfenadine', 'Ketoconazole', 'Major', 'Coadministration with azole antifungal agents may significantly increase the plasma concentrations of terfenadine.  The mechanism is azole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of terfenadine.  High plasma levels of terfenadine have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia and torsade de pointes; cardiac arrest; and sudden death.', 'DDInter', 1767317477, 'The use of terfenadine with most azole antifungal agents, including fluconazole at multiple doses of 400 mg or higher, is considered contraindicated.  Some authorities consider concomitant administration of terfenadine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole (AU).  Loratadine, cetirizine, or fexofenadine may be safer alternatives during therapy with azole antifungal agents.', 'Metabolism', 'The use of terfenadine with most azole antifungal agents, including fluconazole at multiple doses of 400 mg or higher, is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
