INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561923, 'Troleandomycin', 'Terfenadine', 'Major', 'Coadministration with the ketolide, telithromycin, as well as certain macrolide antibiotics may significantly increase the plasma concentrations of astemizole and terfenadine.  The mechanism is inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of astemizole and terfenadine.', 'DDInter', 1767317477, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, the use of these agents with clarithromycin, erythromycin, troleandomycin, or telithromycin is considered contraindicated.  Loratadine, cetirizine, or fexofenadine may be safer alternatives during therapy with telithromycin or macrolides.  Depending on organism susceptibility, azithromycin and dirithromycin may be appropriate alternatives during therapy with astemizole or terfenadine.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, the use of these agents with clarithromycin, erythromycin, troleandomycin, or telithromycin is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561924, 'Carbamazepine', 'Selexipag', 'Moderate', 'Coadministration with inducers of CYP450 2C8 and/or uridine diphosphate glucuronosyltransferase (UGT) 1A3 and/or 2B7 enzymes may reduce the plasma concentrations and therapeutic effects of selexipag and its active metabolite.', 'DDInter', 1767317477, 'Caution and clinical monitoring should be considered whenever a CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducer is added to or withdrawn from therapy, and the selexipag dosage adjusted as necessary.  Some authorities recommend increasing the selexipag dose up to 2-fold when coadministered with rifampin (US).  Patients receiving selexipag with a CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducer should be monitored for reduced efficacy.', 'Metabolism', 'Caution and clinical monitoring should be considered whenever a CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducer is added to or withdrawn from therapy, and the selexipag dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561925, 'Idelalisib', 'Selexipag', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase exposure to the active metabolite of selexipag.  According to the prescribing information, selexipag is hydrolyzed to its active metabolite in the liver and intestine by carboxylesterases, and the active metabolite subsequently undergoes oxidative metabolism catalyzed primarily by CYP450 2C8 and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when selexipag is prescribed with CYP450 2C8 inhibitors.  Dosage adjustments as well as clinical and laboratory monitoring of selexipag may be appropriate whenever a CYP450 2C8 inhibitor is added to or withdrawn from therapy.  Patients should be monitored for potentially increased side effects such as diarrhea, nausea, vomiting, flushing, anemia, and decreased appetite.', 'Metabolism', 'Caution is advised when selexipag is prescribed with CYP450 2C8 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561926, 'Selexipag', 'Phenytoin', 'Moderate', 'Coadministration with inducers of CYP450 2C8 and/or uridine diphosphate glucuronosyltransferase (UGT) 1A3 and/or 2B7 enzymes may reduce the plasma concentrations and therapeutic effects of selexipag and its active metabolite.', 'DDInter', 1767317477, 'Caution and clinical monitoring should be considered whenever a CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducer is added to or withdrawn from therapy, and the selexipag dosage adjusted as necessary.  Some authorities recommend increasing the selexipag dose up to 2-fold when coadministered with rifampin (US).  Patients receiving selexipag with a CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducer should be monitored for reduced efficacy.', 'Metabolism', 'Caution and clinical monitoring should be considered whenever a CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducer is added to or withdrawn from therapy, and the selexipag dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561927, 'Selexipag', 'Pralsetinib', 'Moderate', 'Coadministration with pralsetinib may alter the plasma concentrations of drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5.', 'DDInter', 1767317477, 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.  Some authorities recommend avoiding coadministration of pralsetinib with CYP450 2C8, 2C9, 3A4, and/or 3A5 substrates for which minimal concentration changes may lead to therapeutic failure or serious toxicities.  If coadministration is required, dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever pralsetinib is added to or withdrawn from therapy.  The prescribing information for concomitant medications should be consulted to assess the benefits versus risks of coadministration and for any dosage adjustments that may be required.', 'Metabolism', 'Caution is advised if pralsetinib is used concomitantly with drugs that are substrates of CYP450 2C8, 2C9, 3A4, and/or 3A5, particularly sensitive substrates or those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561928, 'Selexipag', 'Selinexor', 'Moderate', 'Coadministration with selinexor may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B3.  The proposed mechanism, based on in vitro studies, is decreased clearance due to inhibition of OATP1B3 by selinexor.', 'DDInter', 1767317477, 'Caution is advised if selinexor is used concomitantly with drugs that are substrates of OATP1B3, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever selinexor is added to or withdrawn from therapy.', 'Excretion', 'Caution is advised if selinexor is used concomitantly with drugs that are substrates of OATP1B3, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
