INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1561977, 'Selinexor', 'Levodopa', 'Moderate', 'Polyneuropathy has been reported during treatment with levodopa/carbidopa combinations, and concurrent use of other medications that are also associated with this adverse effect may potentiate the risk and/or severity of nerve damage.', 'DDInter', 1767317477, 'Before initiating treatment with enteral formulations of levodopa/carbidopa, the manufacturers recommend evaluating patients for history or signs of polyneuropathy and known risk factors such as diabetes mellitus, hypothyroidism, or concomitant use of other medications associated with polyneuropathy.  For patients with preexisting polyneuropathy, the benefits of treatment should be carefully weighed against the potential risks, including the potential for impaired mobility.  Plasma concentrations of vitamin B12, vitamin B6, homocysteine, methylmalonic acid, and folic acid should be obtained at baseline and at regular intervals during treatment.  Patients who develop symptoms of peripheral neuropathy and low plasma concentrations of vitamin B6 and/or vitamin B12, or elevated homocysteine or methylmalonic acid concentrations, may benefit from vitamin supplementation.  Physicians should carefully evaluate if a dose adjustment is warranted and assess the benefit versus risk of continued treatment.', 'Synergism', 'Before initiating treatment with enteral formulations of levodopa/carbidopa, the manufacturers recommend evaluating patients for history or signs of polyneuropathy and known risk factors such as diabetes mellitus, hypothyroidism, or concomitant use of other medications associated with polyneuropathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
